import axios from 'axios';
export default {
  name: 'ControlPad',
  data: function () {
    return {
      direction: ""
    };
  },
  methods: {
    actuate: function (direction) {
      axios.post('http://' + window.location.hostname + ':8000/actuate', {
        params: {
          direction: this.direction
        }
      }).then(response => this.responseData = response.data);
    }
  }
};
